mapper.select:
select m.memberID, m.memberName, m.memberBirthday, ( case m.memberSex when 1 then '男' when 0 then '女' else '-' end ) as memberSex, m.memberSex, m.memberNation, m.memberIDNumber, m.memberPhone, m.sublineID, ifnull(f.familyName, '-') as memberFamilyID, s.sublineName as sublineGenealogy, m.memberGenealogy, ifnull(mm.memberName, '-') as fatherName, m.memberRelation, m.memberHead, m.memberAddress from membertab m left join familytab f on m.memberFamilyID = f.familyID left join sublinetab s on m.sublineID = s.sublineID left join membertab mm on mm.memberID = m.fatherID where 1 = 1
and m.memberName like concat('%', #{memberName}, '%')
and m.memberPhone like concat('%', #{memberPhone}, '%')
and m.memberIDNumber like concat('%', #{memberIDNumber}, '%')
and m.memberFamilyID = #{memberFamilyID}
and m.sublineID = #{sublineID}
and m.memberSex = #{memberSex}
and m.memberNation = #{memberNation}
order by m.memberID desc
limit #{page}, #{limit}

select
count(m.memberID)
from membertab m
where 1=1

    and m.memberName = #{memberName}


    and m.memberPhone = #{memberPhone}


    and m.memberIDNumber = #{memberIDNumber}


    and m.memberFamilyID = #{memberFamilyID}


    and m.sublineID = #{sublineID}


    and m.memberSex = #{memberSex}


    and m.memberNation = #{memberNation}

mapper.delete:
delete from membertab
where memberID in

    #{item.memberID}
