mapper.select:
select n.newsID, n.newsTitle, n.newsContent, n.newsTime, f.familyName, ad.adminName as createUser, n.newsType from newstab n left join admintab ad on ad.adminID = n.newsFamilyID left join familytab f on n.newsFamilyID = f.familyID where 1 = 1
and n.newsID = #{newsID}
and n.newsFamilyID = #{newsFamilyID}
and n.newsTitle like concat('%', #{newsTitle}, '%')
and n.newsType = #{newsType}
order by newsID desc
limit #{page}, #{limit}

select count(newsID) from newstab
where 1=1

    and newsFamilyID = #{newsFamilyID}


    and newsTitle like concat('%', #{newsTitle}, '%')


    and newsType = #{newsType}

mapper.delete:
delete from newstab
where newsID in

    #{item.newsID}

mapper.update:
update newstab


       newsTitle = #{newsTitle},


       newsContent = #{newsContent},


       newsType = #{newsType}


where 1=1
and newsID = #{newsID}
