mapper.select:
select s.sublineID as subID, s.sublineName as subName, s.sublineGenealogy as subGelogy, s.sublineAddress as subAddress, f.familyName as subFamily, s.sublineCode as subCode from sublinetab s left join familytab f on s.sublineFamilyID = f.familyID where 1 = 1
and s.sublineGenealogy like concat('%', #{subGelogy}, '%')
and s.sublineName like concat('%', #{subName}, '%')
and s.sublineAddress like concat('%', #{subAddress}, '%')
and s.sublineFamilyID = #{subFamilyID}
and s.sublineID = #{sublineID}
order by s.sublineID desc
limit #{page}, #{limit}

mapper.delete:
delete from sublinetab
where sublineID in

    #{item.subID}
